import React, {Component} from 'react';
import { connect} from 'react-redux';
import './index.css';
import axios from 'axios';
import OrderModal from './orderModal';

import {fetchAllOrders} from './actions/fetchAllOrders';
import openSocket from 'socket.io-client';

const socket = openSocket('http://127.0.0.1:4000');










class WaiterView extends React.Component {
  constructor(){
    super();

  
   
    }
componentDidMount(){
this.props.fetchAllOrders();

socket.on('orderReceived', () =>{

  this.props.fetchAllOrders();
});
}


openModal = ()=>{
 this.props.openModal();

}


  render(){

    let ordersList = this.props.orders.map((item, index) =>{

      return <li className=""onClick={() => this.openModal} key={index}>{item.time}<span className="" ></span><span className=""> {item._id} </span></li>
    });
   
    return (
    <div className="checkOutMain">
    <h2>New orders</h2>
    <ul>
        {ordersList}

    </ul>

    <OrderModal></OrderModal>
    </div>
  );
    }
}

const mapStateToProps = (state) =>{

    return{
       
        
      orders:state.orders
  
  
      
    }
  
  }
const mapDispatchToProps = (dispatch) =>{

  return{
        
       
    fetchAllOrders: () =>{dispatch(fetchAllOrders())},
    openModal:()=>{dispatch({type: 'OPEN_MODAL'})}

    


      

  }
}



export default connect(mapStateToProps, mapDispatchToProps)(WaiterView);
