import React, {Component} from 'react';
import { connect} from 'react-redux';
import './index.css';
import axios from 'axios';











class OrderModal extends React.Component {
  constructor(){
    super();

  
   
    }
closeModal= () =>{

    this.props.closeModal();
}


  render(){
    if (this.props.order.contents == undefined)
    return null;

    
  let orderContents = this.props.order.contents.map((item, index) =>{
    return  <p className="orderListItem" key={item.id}>{item.name}<span>{item.price} kn</span></p>

   });
   
    return (
        <div id="myModal" style={{ display: this.props.openModal ? 'block': 'none'}}className="modal">
        <div className="modal-content">
        <div className="modal-header">
          <span className="close" onClick={this.closeModal}>&times;</span>
          <span className="orderId">{this.props.order._id}</span><span className="orderTime">{this.props.order.time}</span>
        </div>
        <div className="modal-body">
        <h3>Order summary:</h3>
        <div className="orderReview">
        {orderContents}

        <p className="total">TOTAL: <span>{this.props.order.price} kn</span></p>
    </div>
        </div>
        <div className="modal-footer">
          <h3>Modal Footer</h3>
        </div>
      </div>
      </div>
  
    )
}
}
const mapStateToProps = (state) =>{

    return{
       
        
      openModal:state.openModal,
    order:state.modalOrder
      
    }
  
  }
const mapDispatchToProps = (dispatch) =>{

  return{
        
       
    

    closeModal:()=>{dispatch({type: 'CLOSE_MODAL'})}



      

  }
}



export default connect(mapStateToProps, mapDispatchToProps)(OrderModal);
