const initState={

    alcoholicDrinks: [],
    nonAlcoholicDrinks: [],
    beer: [],
    snacks: []
}


const rootReducer = (state = initState, action) =>{

    if (action.type === 'SET_ALCOHOLIC_DRINKS'){
        console.log(action.data);
          
      return{
           ...state,
           alcoholicDrinks:[action.alcoholicDrinks]

      } 
   }

return state;

}

export default rootReducer;
