  render(){

   
   
    return (
        <div id="myModal" style={{ display: this.props.openModal ? 'block': 'none'}}className="modal">
        <div className="modal-content">
        <div className="modal-header">
          <span className="close" onClick={this.closeModal}>&times;</span>
          <h2>Modal Header</h2>
