import axios from 'axios';


export const fetchAllOrders = () =>{
    return (dispatch) => {

        axios.get('http://localhost:4000/api/getAllOrders',)
            .then(response => {

                  console.log(response.data);
                   dispatch({
                    type: 'SET_ORDERS_FROM_DB',
                    orders: response.data
                })
              
            })
            
            .catch(error => {
                console.log(error);
             
            });

        
    }

}
