import React, {Component} from 'react';
import { connect} from 'react-redux';
import './index.css';

import logo from './logo.svg';
import './App.css';

import DajanHeader from './dajanHeader';





class Checkout extends React.Component {
  constructor(){
    super();

    this.state={

    email:''
    }
   
    }

setEmail = (e) =>{

    if(e.target.name == 'email'){

        this.setState({

        email:e.target.value
    })
}
}
    

  render(){

    let orderFinalPrice=0;
    let order = this.props.activeOrder.map(item =>{
           orderFinalPrice+=item.price;
       return  <p className="orderListItem" key={item.id}>{item.name}<span>{item.price} kn</span></p>
    });
    return (
    <div className="checkOutMain">
     
        <h3>Order summary:</h3>
        <div className="">
            {order}

        <p className="total">TOTAL: <span>{orderFinalPrice} kn</span></p>
    </div>

    <h6 className="enterEmailH6">Enter your email address in the field below and you will get your receipt e-mailed to you (optional)</h6>
    <input type="email" name="email" value={this.state.email} onChange={this.setEmail}/>

    
    </div>
  );
    }
}

const mapStateToProps = (state) =>{

    return{
        activeOrder: state.activeOrder
        
  
  
  
      
    }
  
  }
const mapDispatchToProps = (dispatch) =>{

  return{
        
       



      

  }
}



export default connect(mapStateToProps, mapDispatchToProps)(Checkout);
