import React, {Component} from 'react';
import { connect} from 'react-redux';
import './index.css';

import logo from './logo.svg';



class Menu extends React.Component {
  constructor(){
    super();
   
    }

closeCurtainMenu = () => {

  this.props.closeMenu();

  
}

addToBasket = () =>{

}
    
    

  render(){

    if(this.props.alcoholicDrinks ==undefined)
            return null;
      
            let alcoholicDrinksList = this.props.alcoholicDrinks.map((item, index) =>{

              return <p className="menuItem" key={index}>{item.name}<span className="dots">..............</span><span className="prices"> {item.price}</span><button className="addButton" onClick={this.addToBasket(item.id)}>Add</button></p>
            });
    return (
    <div>

<div id="myNav" className={this.props.openMenu ?   'overlayOpen' : 'overlay'}>
<div className="curtainMenuCart"></div>
<span className="shoppingCartItemNumber">1</span>

<a className="closebtn" onClick={this.closeCurtainMenu}>&times;</a>




<div className="overlay-content">

<div className="accordion">Non-alcholic drinks</div>
<div className="panel">
  <p>Lorem ipsum...</p>
</div>

<div className="accordion">Alcholic drinks</div>
<div className="panel">
  {alcoholicDrinksList}
  
</div>

<div className="accordion">Beer</div>
<div className="panel">
  <p>Lorem ipsum...</p>
</div>

<div className="accordion">Snacks</div>
<div className="panel">
  <p>Lorem ipsum...</p>
</div>
  
</div>





</div>
     
    </div>
  );
    }
}


const mapStateToProps = (state) =>{

  return{
      openMenu: state.openMenu,
      alcoholicDrinks: state.alcoholicDrinks
      


    
  }
}

const mapDispatchToProps = (dispatch) =>{

  return{
        
    closeMenu:()=>{dispatch({type: 'CLOSE_CURTAIN_MENU'})}
      

  }
}



export default connect(mapStateToProps, mapDispatchToProps)(Menu);
